<?php$item_position = (int)$_POST['item_position'];$item_length = ITEM_LENGTH;$current_items = warehouse_items($username);$items	= substr($current_items,2);$i = -1;$num=0;while($i < 119){	$i++;	$item 	= ItemInfo(substr($items,($item_length*$i), $item_length));	if($i == $item_position && $num==0)	{		$cat_id = $item['item_category'];		$item_id = $item['item_id'];		$olditem=substr($items,($item_length*$i), $item_length);		$items= substr_replace($items,str_repeat('F',$item_length),($i*$item_length), $item_length);		$num++;	}}$item_info 	= ItemInfo($olditem);if(is_banned($item_info['item']) === true){	show_msg('This item can&#39;t be sold!');}elseif(isset($_POST['sellitem']) && $_POST['sellitem'] === '1'){	$price = (int)$_POST['item_price'];	$added = time();	$expires = ($added + (86400 * $market['item_remove_in']));	if(!isset($item_info['name']))	{		show_msg('Invalid Item!');	}	elseif(empty($price))	{		show_msg('You must put a price!');	}	elseif($price < 10)	{		show_msg('You must put a price more than 10!');	}	else	{		mssql_query("UPDATE [warehouse] SET [items]=0x".$items." WHERE [AccountId]='".$username."'");		mssql_query("INSERT INTO [MarketPlace] (cat_id,item_id,item,serial,price,seller,added_on,expires_on,X,Y) VALUES ('".$cat_id."','".$item_id."','".$olditem."','".$item_info['sn']."','".$price."','".$username."','".$added."','".$expires."','".$item_info['X']."','".$item_info['Y']."')");		show_msg('You sold '.$item_info['name'].' for '.$price.' credits.',1);	}}else{?><form method="post" action="?p=market"><table style="text-align:center;"><tr>	<td>	<?php 		$plusche=($item_info['level']) ? '+'. $item_info['level'] : '';		echo $item_info['name'].' '.$plusche; 	?>	</td></tr><tr>	<td style="min-height:120px;">		<div class="item_info"><?php echo $item_info['no_name']; ?></div>	</td></tr><tr>	<td><p class="left">Item Size:</p> <p class="right" style="color:#999;"><?php echo $item_info['X']; ?> x <?php echo $item_info['Y']; ?></p></td></tr><tr>	<td><p class="left">Item Price:</p> <p class="right" style="color:#999;"><input type="text" size="10" name="item_price" maxlength="11" onkeyup="price(this.value, <?php echo $market['multiplier']; ?>); return isNumber(this)"/> Credits</p></td></tr><tr>	<td><p class="left">Market Price:</p> <p class="right" style="color:#999;" id="merket_price">0</p></td></tr><tr>	<td style="text-align:center;">		<input type="hidden" name="item_position" value="<?php echo $item_position; ?>" />		<input type="hidden" name="sellitem" value="1" />		<input type="submit" class="button" value="Sell <?php echo $item_info['name']; ?>" />	</td></tr></table></form><?php}?>